mapper.update:
UPDATE NOTI
SET COMMON_CODE_NOTI_KIND = 'NOTI021'
WHERE NOTI_ETPR_CODE = #{notiEtprCode}

UPDATE NOTI
SET NOTI_TITLE                   = #{notiTitle},
    NOTI_CONTENT                 = #{notiContent},
    NOTI_DATE                    = sysdate,
    NOTI_CTGRY_ICON_FILE_STRE_NM = #{notiCtgryIconFileStreNm}
WHERE NOTI_ETPR_CODE = #{notiEtprCode}
