mapper.select:
SELECT
        #{formatSanctnKnd} || '-' || TO_CHAR(SYSDATE, 'YYYY') || '-' || LPAD(
            (SELECT TO_NUMBER(COUNT(*)) + 1
             FROM ELCTRN_SANCTN
             WHERE SUBSTR(ELCTRN_SANCTN_ETPR_CODE, 1, INSTR(ELCTRN_SANCTN_ETPR_CODE, '-') - 1) = #{formatSanctnKnd}
            ),
            4, '0'
        ) AS elctrnSanctnEtprCode
FROM DUAL
